//these will eventually be generated by JS and object properties

function createBanner(domNode,settings) {
  this.layout;//defines the layout structure
  this.node = domNode;//grabs the node to attach to
  this.promo = settings.promoNumber;//sets the banner object type
  this.url = settings.url;//wraps the banner in the 
  this.header1 = settings.header1;//defines the header text
  this.header2 = settings.header2;//defines the header text
  this.header3 = settings.header3;//defines the header text
  this.preheader = settings.preheader;//defines the preheader text
  this.subtitle = settings.subtitle;//defines the subtitle text
  this.background = settings.background;


  this.createMe = function(){ //actually makes the banner
    if (this.promo == 1) {
      this.layout = $('<div class="banner-wrapper promo-1"><a href="#"><div class="one-col"><div class="two-three-col"><div class="col-inner"><p class="header-1"><span class="enjoy"></span><em></em></p></div></div><div class="one-three-col"><div class="col-inner"><p class="pre-header"></p><p class="sub-title"><span class="standout"></span></p></div></div></div></a></div>');
      var n = $('.'+this.node); //set the node
      n.append(this.layout); //append the layout to the DOM
      n.find('.banner-wrapper').css('background',this.background);
      n.find('a').eq(0).attr('href',this.url);//wrap the banner in your chosen URL
      n.find('.header-1').html(this.header1);
      n.find('.pre-header').html(this.preheader);
      n.find('.sub-title').html(this.subtitle);
      

    };
    if (this.promo == 2) {
      this.layout = $('<div class="banner-wrapper promo-2"><a href="#"><div class="one-col"><div class="one-two-col"><div class="col-inner"><p class="header-1"><span class="enjoy"></span><em></em></p></div></div><div class="one-two-col"><div class="col-inner"><p class="pre-header"></p><p class="sub-title"><span class="standout"></span></p></div></div></div></a></div>');
      $('.'+this.node).append(this.layout);
      var n = $('.'+this.node); //set the node
      n.append(this.layout); //append the layout to the DOM
      n.find('.banner-wrapper').css('background',this.background);
      n.find('a').eq(0).attr('href',this.url);//wrap the banner in your chosen URL
      n.find('.header-1').html(this.header1);
      n.find('.pre-header').html(this.preheader);
      n.find('.sub-title').html(this.subtitle);      
    };
    if (this.promo == 3) {
      this.layout = $('<div class="banner-wrapper promo-3"><a href="#"><div class="one-col"><div class="one-col"><div class="col-inner"><p class="header-1"><span class="enjoy"></span><em></em></p></div></div><div class="one-col"><div class="col-inner"><p class="pre-header"></p><p class="sub-title"><span class="standout"></span></p></div></div></div></a></div>');
      $('.'+this.node).append(this.layout);
      var n = $('.'+this.node); //set the node
      n.append(this.layout); //append the layout to the DOM
      n.find('.banner-wrapper').css('background',this.background);
      n.find('a').eq(0).attr('href',this.url);//wrap the banner in your chosen URL
      n.find('.header-1').html(this.header1);
      n.find('.pre-header').html(this.preheader);
      n.find('.sub-title').html(this.subtitle);       
    };
    if (this.promo == 4) {
      this.layout = $('<div class="banner-wrapper promo-4"><a href="#"><div class="one-col"><div class="one-three-col"><div class="col-inner"><p class="header-1"><span class="enjoy"></span><em></em></p></div></div><div class="one-three-col"><div class="col-inner"><p class="header-2"><span class="enjoy"></span><em></em></p></div></div><div class="one-three-col"><div class="col-inner"><p class="header-3"><span class="enjoy"></span><em></em></p></div></div></div></a></div>');
      $('.'+this.node).append(this.layout);
      var n = $('.'+this.node); //set the node
      n.append(this.layout); //append the layout to the DOM
      n.find('.banner-wrapper').css('background',this.background);
      n.find('a').eq(0).attr('href',this.url);//wrap the banner in your chosen URL
      n.find('.header-1').html(this.header1);
      n.find('.header-2').html(this.header2);
      n.find('.header-3').html(this.header3);
      n.find('.pre-header').html(this.preheader);
      n.find('.sub-title').html(this.subtitle);      
    };            
  };
  this.createMe();
};

//end of logic

